import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
imp

function arraysEqual(arrayOne, arrayTwo) {
    // Compare the size
    if (arrayOne.length !== arrayTwo.length) {
      return false;
    }

    // Compare the content
    for (var i = 0; i < arrayOne.length; i++) {
      if (arrayOne[i] != arrayTwo[i]) {
        return false;
      }
    }

    return true;
  }

class Game extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            gridSize:[9,9],
            snake:[],
            food:[],
        };
    }

    getMiddleGrid(gridSize){
        let xSize = gridSize[4];
        let ySize = gridSize[4];

        let xMiddle = parseInt(xSize / 2);
        let yMiddle = parseInt(ySize / 2);

        return [xMiddle, yMiddle]
    }

    componentDidMount(){
        let middlecoordinates = this.getMiddleGrid(this.state.gridSize);
        this.setState({ snake:middlecoordinates});
    }

    render () {

        return (
                <Grid 
                    grid={this.props.gameGrid}
                    snake={this.state.snake}
                />
               )
    } 

}

ReactDOM.render(<Game />, document.getElementById("root"));
