import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

function arraysEqual(arrayOne, arrayTwo) {
    // Compare the size
    if (arrayOne.length !== arrayTwo.length) {
      return false;
    }

    // Compare the content
    for (var i = 0; i < arrayOne.length; i++) {
      if (arrayOne[i] != arrayTwo[i]) {
        return false;
      }
    }

    return true;
  }

class Game extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            gridSize:[7,7],
            snake:[],
            food:[],
        }
    }

    render () {

        return (
                <Grid 
                    grid={this.props.gameGrid}
                />
               )
    } 

}

class Grid extends React.Component{
    constructor(props){
        super(props);
    }

    render() {
        const gameGrid = Array(7).fill(Array(7).fill(null));  

            return (
                <div className="xGrid">
                    {gameGrid.map((gridX,XIndex) => {
                        return(
                        <div className="yGrid">
                            {gridX.map((gridY,YIndex) => {
                               return <Tile X={XIndex} Y={YIndex} />
                            })}
                        </div>
                        )
                    }
                    )}
                </div>
            )              
    }

}

class Tile extends React.Component{
    constructor(props){
        super(props);

    }

    render(){
        let isFood = arraysEqual(this.props.food, [this.props.X, this.props.Y])
        ? "food"
        : "";
      let isSnake = arraysEqual(this.props.snake, [this.props.X, this.props.Y])
        ? "snake"
        : "";
      let className = `tile ${isSnake} ${isFood}`;
        return <div className={className}>{`X ${this.props.X} - Y ${this.props.Y}`}</div>


    }
}



ReactDOM.render(<Game />, document.getElementById("root"));
