import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

class Game extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            gridSize:[9,9],
            snake:[],
            food:[],
        };
    }

    getMiddleGrid(gridSize){
        let xSize = gridSize[4];
        let ySize = gridSize[4];

        let xMiddle = parseInt(xSize / 2);
        let yMiddle = parseInt(ySize / 2);

        return [xMiddle, yMiddle]
    }

    componentDidMount(){
        let middlecoordinates = this.getMiddleGrid(this.state.gridSize);
        this.setState({ snake:middlecoordinates});
    }

    render () {

        return (
                <Grid 
                    grid={this.props.gameGrid}
                    snake={this.state.snake}
                />
               )
    } 

}

class Grid extends React.Component{
    constructor(props){
        super(props);
    }

    render() {
        const gameGrid = Array(9).fill(Array(9).fill(null));  

            return (
                <div className="xGrid">
                    {gameGrid.map((gridX,XIndex) => {
                        return(
                        <div className="yGrid">
                            {gridX.map((gridY,YIndex) => {
                               return <Tile X={XIndex} Y={YIndex} snake={this.props.snake}/>
                            })}
                        </div>
                        )
                    }
                    )}
                </div>
            )              
    }
}

class Tile extends React.Component{
    constructor(props){
        super(props);
        this.state={
            snake: [3,3],
        };
        let isSnake = arraysEqual(this.props.snake, [this.props.X, this.props.Y])
        ? "snake"
        : "";
    }


    render(){
        return <div className={"tile"} id={this.props.X , this.props.Y}>{`X ${this.props.X} - Y ${this.props.Y}`}</div>
    }
}



ReactDOM.render(<Game />, document.getElementById("root"));
