import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import Grid from "./constructors/Grid";

class Game extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            gridSize:[9,9],
            snake:[],
            apple:[],
            movmentTo: "up",
            time:0
        };
    }

    // Howtomove(){
    //     window.addEventListener('W', handleKey());
    //     window.addEventListener('A', handleKey());
    //     window.addEventListener('S', handleKey());
    //     window.addEventListener('D', handleKey());

    //     handleKey =(event)=>{
    //         if(event.key === 'W'){
    //             console.log('w')
    //           }

    //     }
    // }

    timeclock(){
    setInterval(this.gametick(), 500)
    }

    gametick(){
        let hedSnake =[];
        let tmp = 0;
        const gridSizX = this.state.gridSize[0];
        const gridSizY = this.state.gridSize[1];

        switch(this.state.movmentTo){           

            case "up":
                tmp = this.state.snake[0] - 1 < 0? gridSizX - 1 :this.state.snake[0] - 1;
                hedSnake= [tmp, this.state.snake[1] ];
                break;
            case "down":
                tmp = (this.state.snake[0] + 1) % gridSizX;
                hedSnake = [tmp, this.state.snake[1] ];
                break;
            case "left":
                tmp = this.state.snake[1] - 1 < 0? gridSizY - 1 :this.state.snake[1] - 1;
                hedSnake = [this.state.snake[0], tmp ];
                break;
            case "right":
                tmp = (this.state.snake[1] + 1) % gridSizX;
                hedSnake = [this.state.snake[0], tmp ];
                break;

                default:
                    hedSnake = [parseInt(this.state.gridSize[0]/2), parseInt(this.state.gridSize[1]/2)];
            }
        this.setState({snake: newSnake, time: this.state.time + 1});
    }

    getRandomApple(gridSize){
        let xSize = gridSize[0]
        let ySize = gridSize[1]

        let xApple = Math.floor(Math.random()*xSize);
        let yApple = Math.floor(Math.random()*ySize);

        let xSnake = this.state.snake[0];
        let ySnake = this.state.snake[1];

        if(xApple === xSnake && yApple === ySnake){
            return this.getRandomApple(gridSize)
        }
        else {
            return [xApple,yApple]
        }
    }


    getMiddleOfCordsSnake(gridSize){
        let xSize = gridSize[0]
        let ySize = gridSize[1]

        let xMiddle = parseInt(xSize / 2);
        let yMiddle = parseInt(ySize / 2);

        return [xMiddle, yMiddle];

    }

    componentDidMount() {
        let ValofmiddleCoordinates = this.getMiddleOfCordsSnake(this.state.gridSize);
        this.setState({ snake: ValofmiddleCoordinates });

        let ValofrandomApple = this.getRandomApple(this.state.gridSize);
        this.setState({ apple: ValofrandomApple });
      }


    render () {

        return (
                <Grid 
                    grid={this.props.gameGrid}
                    snake={this.state.snake}
                    apple={this.state.apple}
                />
               )
    } 

}

ReactDOM.render(<Game />, document.getElementById("root"));
